package part01.sec01.exam01;

public class Ex04_06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 99;
		System.out.printf("AND 연산:%s\n", (a >= 100) && (a <= 200));// false
		System.out.printf("OR 연산:%s\n", (a >= 100) || (a <= 200));// true
		System.out.printf("NOT 연산:%s\n", !(a == 100));// true
	}

}
