package part01.sec01.exam01;

public class Ex07_05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 100;
		while (a == 200) {
			System.out.println("while문 내부에 들어왔습니다.");
		}
		do {
			System.out.println("do~while문 내부에 들어왔습니다.");
		} while (a == 200);


	}

}
